    const [value, setValue] = useState("")

    function addToDo(e) {
        if(value !== ''){
            e.preventDefault()
            const{}=useCustomFetch({
                method: 'POST',
               url: url+'/posts', 
               header: {"content-type": "application/json"},
               data: value
            })
    }

